package whileloop;

public class DoWhileLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 1;

		do {
			System.out.println("Hello");
			n++;
		} while (n < 4);

		int k = 5;

		do {
			System.out.println("HELLO");
			k++;
		} while (k < 4);
	}
}
